mapper.select:
select	plan_id, p_title, p_start_date, p_end_date, p_groupno, p_map_img_path, 
		t_name, a_name, a_address
from(

)
where 	p_title like '%'||#{searchWord}||'%' or t_name like '%'||#{searchWord}||'%'
		or a_name like '%'||#{searchWord}||'%'

select	plan_id, p_title, p_start_date, p_end_date, p_groupno, 
	   	p_map_img_path, t_name, a_name, a_address
from(
		select	ceil(rownum/#{contentsPerPage}) page, plan_id, p_title,
			 	p_start_date, p_end_date, p_groupno, p_map_img_path, t_name, a_name, a_address
		from(
				select	plan_id, 
						p_title, 
						p_start_date, 
						p_end_date, 
						p_groupno, 
						p_map_img_path, 
						t_name, 
						a_name, 
						a_address
				from(

				)
				where 	p_title like '%'||#{searchWord}||'%' or t_name like '%'||#{searchWord}||'%'
						or a_name like '%'||#{searchWord}||'%'
				order by plan_id
		)
)
where page = #{pageNo}

select	count(plan_id)
from(

	)
where 	p_title like '%'||#{searchWord}||'%' or t_name like '%'||#{searchWord}||'%'
		or a_name like '%'||#{searchWord}||'%'

select	posting_id, p_content, p_title, p_number, p_like, p_hits, p_img_path, boardtype_id, member_id
from(

)
where	p_content like '%'||#{searchWord}||'%' or p_title like '%'||#{searchWord}||'%'

select	posting_id, p_content, p_title, p_number, p_like, p_hits, p_img_path, boardtype_id,
		member_id
from(
		select	ceil(rownum/#{contentsPerPage}) page, posting_id, p_content, p_title, p_number,
				p_like, p_hits, p_img_path, boardtype_id, member_id
		from(
				select	posting_id, p_content, p_title, p_number, p_like, p_hits, 
						p_img_path, boardtype_id, member_id
				from(

				)
				where	p_content like '%'||#{searchWord}||'%' or p_title like '%'||#{searchWord}||'%'
				order by posting_id	
		)
)
where page = #{pageNo}

select	count(posting_id)
from(

	)
where	p_content like '%'||#{searchWord}||'%' or p_title like '%'||#{searchWord}||'%'

select	plan_id, p_title, p_start_date, p_end_date, p_groupno, p_map_img_path, 
		t_name, a_name, a_address
from(

)
where	1=1

			and p_title like '%'||#{title}||'%'


			and t_name like '%'||#{theme}||'%'


			and a_name like '%'||#{attraction}||'%'


			and p_groupno = #{number}

select	plan_id, p_title, p_start_date, p_end_date, p_groupno, 
	   	p_map_img_path, t_name, a_name, a_address
from(
		select	ceil(rownum/#{contentsPerPage}) page, plan_id, p_title,
			 	p_start_date, p_end_date, p_groupno, p_map_img_path, t_name, a_name, a_address
		from(
				select	plan_id, 
						p_title, 
						p_start_date, 
						p_end_date, 
						p_groupno, 
						p_map_img_path, 
						t_name, 
						a_name, 
						a_address
				from(

				)
				where	1=1

							and p_title like '%'||#{title}||'%'


							and t_name like '%'||#{theme}||'%'


							and a_name like '%'||#{attraction}||'%'


							and p_groupno = #{number}

				order by plan_id
		)
)
where page = #{pageNo}

